// SPDX-License-Identifier: MIT

pragma solidity >=0.8.0 <0.9.0;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title DEX Template
 * @author stevepham.eth and m00npapi.eth
 * @notice Empty DEX.sol that just outlines what features could be part of the challenge (up to you!)
 * @dev We want to create an automatic market where our contract will hold reserves of both ETH and ðŸŽˆ Balloons. These reserves will provide liquidity that allows anyone to swap between the assets.
 * NOTE: functions outlined here are what work with the front end of this challenge. Also return variable names need to be specified exactly may be referenced (It may be helpful to cross reference with front-end code function calls).
 */
contract DEX {
	/* ========== GLOBAL VARIABLES ========== */

	IERC20 token; //instantiates the imported contract

	/* ============= ERRORS ============= */
	error HasAlreadyLiquidity();
	error SentTokenFailure();
	error SentETHFailure();
	error NotEnoughETHSent();
	error NotEnoughBALSent();
	error NotEnoughLiquidity();

	/* ========== EVENTS ========== */

	/**
	 * @notice Emitted when ethToToken() swap transacted
	 */
	event EthToTokenSwap(
		address swapper,
		uint256 tokenOutput,
		uint256 ethInput
	);

	/**
	 * @notice Emitted when tokenToEth() swap transacted
	 */
	event TokenToEthSwap(
		address swapper,
		uint256 tokensInput,
		uint256 ethOutput
	);

	/**
	 * @notice Emitted when liquidity provided to DEX and mints LPTs.
	 */
	event LiquidityProvided(
		address liquidityProvider,
		uint256 liquidityMinted,
		uint256 ethInput,
		uint256 tokensInput
	);

	/**
	 * @notice Emitted when liquidity removed from DEX and decreases LPT count within DEX.
	 */
	event LiquidityRemoved(
		address liquidityRemover,
		uint256 liquidityWithdrawn,
		uint256 tokensOutput,
		uint256 ethOutput
	);

	/* ============ MAPPING ============ */
	mapping (address => uint256) public liquidity;

	/* ============ VARIABLES =========== */
	uint256 public totalLiquidity;


	/* ========== CONSTRUCTOR ========== */

	constructor(address tokenAddr) {
		token = IERC20(tokenAddr); //specifies the token address that will hook into the interface and be used through the variable 'token'
	}

	/* ========== MUTATIVE FUNCTIONS ========== */

	/**
	 * @notice initializes amount of tokens that will be transferred to the DEX itself from the erc20 contract mintee (and only them based on how Balloons.sol is written). Loads contract up with both ETH and Balloons.
	 * @param tokens amount to be transferred to DEX
	 * @return totalLiquidity is the number of LPTs minting as a result of deposits made to DEX contract
	 * NOTE: since ratio is 1:1, this is fine to initialize the totalLiquidity (wrt to balloons) as equal to eth balance of contract.
	 */
	function init(uint256 tokens) public payable returns (uint256) {
		// Check if sender has no liquidity to init
		if (totalLiquidity != 0) {
			revert HasAlreadyLiquidity();
		}
		// Init total liquidity of the contract
		totalLiquidity = address(this).balance;
		// Init liquidity of the sender
		liquidity[msg.sender] = totalLiquidity;
		// Init token balance on the contract DEX from Ballon contract
		bool sentToken = token.transferFrom(msg.sender, address(this), tokens);
		if (!sentToken) {
			revert SentTokenFailure();
		}
		// Return total liquidity of DEX contract
		return totalLiquidity;
	}

	/**
	 * @notice returns yOutput, or yDelta for xInput (or xDelta)
	 * @dev Follow along with the [original tutorial](https://medium.com/@austin_48503/%EF%B8%8F-minimum-viable-exchange-d84f30bd0c90) Price section for an understanding of the DEX's pricing model and for a price function to add to your contract. You may need to update the Solidity syntax (e.g. use + instead of .add, * instead of .mul, etc). Deploy when you are done.
	 */
	function price(
		uint256 xInput,
		uint256 xReserves,
		uint256 yReserves
	) public pure returns (uint256 yOutput) {
		/**
		 *	Applying Uniswap V2 automated market maker (AMM)
		 *	https://hackernoon.com/formulas-of-uniswap-a-deep-dive
		 *  x * y = x(new) * y(new) = k
		 */
		uint256 xInputWithFee = xInput * 997;
		uint256 xNewReserves = (xReserves * 1000) + xInputWithFee; // to apply the right fee
		return (xInputWithFee * yReserves) / xNewReserves;
	}

	/**
	 * @notice returns liquidity for a user.
	 * NOTE: this is not needed typically due to the `liquidity()` mapping variable being public and having a getter as a result. This is left though as it is used within the front end code (App.jsx).
	 * NOTE: if you are using a mapping liquidity, then you can use `return liquidity[lp]` to get the liquidity for a user.
	 * NOTE: if you will be submitting the challenge make sure to implement this function as it is used in the tests.
	 */
	function getLiquidity(address lp) public view returns (uint256) {
		return liquidity[lp];
	}

	/**
	 * @notice sends Ether to DEX in exchange for $BAL
	 */
	function ethToToken() public payable returns (uint256 tokenOutput) {
		// Check if minimum value of ETH sent
		if (msg.value == 0) {
			revert NotEnoughETHSent();
		}
		// Init variable for price function 
		uint256 xReserveETH = address(this).balance - msg.value;
		uint256 yReserveBAL = token.balanceOf(address(this));
		// Get token output from price converter
		uint256 outputBAL = price(msg.value, xReserveETH, yReserveBAL);
		// Send token to the sender
		bool tokenSent = token.transfer(msg.sender, outputBAL);
		if (!tokenSent) {
			revert SentTokenFailure();
		}
		emit EthToTokenSwap(msg.sender, outputBAL, msg.value);
		return outputBAL;
	}

	/**
	 * @notice sends $BAL tokens to DEX in exchange for Ether
	 */
	function tokenToEth(
		uint256 tokenInput
	) public returns (uint256 ethOutput) {
		// Check if minimum value of BAL sent
		if (tokenInput == 0) {
			revert NotEnoughBALSent();
		}
		// Init variable for price function 
		uint256 xReserveBAL = token.balanceOf(address(this));
		uint256 yReserveETH = address(this).balance;
		// Get ETH output from price converter
		uint256 outputETH = price(tokenInput, xReserveBAL, yReserveETH);
		// Send token to DEX
		bool tokenSent = token.transferFrom(msg.sender, address(this), tokenInput);
		if (!tokenSent) {
			revert SentTokenFailure();
		}
		// Send back eth to the sender
		(bool sentETH,) = payable(msg.sender).call{ value: outputETH }("");
		if (!sentETH) {
			revert SentETHFailure();
		}
		emit TokenToEthSwap(msg.sender, tokenInput, outputETH);
		return outputETH;
	}

	/**
	 * @notice allows deposits of $BAL and $ETH to liquidity pool
	 * NOTE: parameter is the msg.value sent with this function call. That amount is used to determine the amount of $BAL needed as well and taken from the depositor.
	 * NOTE: user has to make sure to give DEX approval to spend their tokens on their behalf by calling approve function prior to this function call.
	 * NOTE: Equal parts of both assets will be removed from the user's wallet with respect to the price outlined by the AMM.
	 */
	function deposit() public payable returns (uint256 tokensDeposited) {
		// Check if minimum value of ETH sent
		if (msg.value == 0) {
			revert NotEnoughETHSent();
		}
		// Init reserve variable
		uint256 ethReserve = address(this).balance - msg.value;
		uint256 balReserve = token.balanceOf(address(this));
		// Get token deposited
		// NOTE : (eth * bal) / eth => bal (maybe the logic)
		uint256 tokenDeposit = ((msg.value * balReserve) / ethReserve) + 1; // To avoid negative numbers ?
		// Get the liquidity minted from the deposit
		// NOTE : (eth * eth) / eth => eth
		uint256 liquidityMinted = (msg.value * totalLiquidity) / ethReserve;
		// Update global variable for liquidity
		liquidity[msg.sender] += liquidityMinted;
		totalLiquidity += liquidityMinted;
		// Send token and check if amount is approved
		bool tokenSent = token.transferFrom(msg.sender, address(this), tokenDeposit);
		if (!tokenSent) {
			revert SentTokenFailure();
		}
		// Emit and return
		emit LiquidityProvided(msg.sender, liquidityMinted, msg.value, tokenDeposit);
		return tokenDeposit;
	}

	/**
	 * @notice allows withdrawal of $BAL and $ETH from liquidity pool
	 * NOTE: with this current code, the msg caller could end up getting very little back if the liquidity is super low in the pool. I guess they could see that with the UI.
	 */
	function withdraw(
		uint256 amount
	) public returns (uint256 ethAmount, uint256 tokenAmount) {
		if (amount > liquidity[msg.sender]) {
			revert NotEnoughLiquidity();
		}
		// Init reserve variable
		uint256 ethReserve = address(this).balance;
		uint256 balReserve = token.balanceOf(address(this));
		uint256 ethWithdrawn;
		// Computed corresponding amounts
		ethWithdrawn = (amount * ethReserve) / totalLiquidity;
		tokenAmount = (amount * balReserve) / totalLiquidity;
		// Apply withdraw effect on liquidity
		liquidity[msg.sender] -= ethWithdrawn;
		totalLiquidity -= ethWithdrawn;
		// Pay back the user of eth
		(bool ethSent,) = payable(msg.sender).call{ value: ethWithdrawn }("");
		if (!ethSent) {
			revert SentETHFailure();
		}
		// Send back token to the sender
		bool tokenSent = token.transfer(msg.sender, tokenAmount);
		if (!tokenSent) {
			revert SentTokenFailure();
		}
		// Emit and returns
		emit LiquidityRemoved(msg.sender, amount, tokenAmount, ethWithdrawn);
		return (ethWithdrawn, tokenAmount);
	}
}
